    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed

        Produto prod = new Produto();

        if ((txtProduto.getText().equalsIgnoreCase("")) || (txtMarca.getText().equalsIgnoreCase("")) || (txtPreco.getText().equalsIgnoreCase(""))) {

            try {

                throw new CamposVaziosException();

            } catch (CamposVaziosException c) {
            };

        } else {

            prod.setNome(txtProduto.getText());
            prod.setMarca(txtMarca.getText());
            prod.setTipo(cmbTipo.getSelectedItem().toString());
            prod.setPreco(Float.parseFloat(txtPreco.getText()));

            arq.addProduto(prod);
            arq.ordenarProdutos();
            limpar();
        }

    }//GEN-LAST:event_btnAddActionPerformed

    private void btnListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnListarActionPerformed

        produtos = arq.buscarProduto();

        jLabelTotal.setText(produtos.size() + "");

        dlm.clear();
        for (int i = 0; i < produtos.size(); i++) {
            dlm.addElement("Nome: " + produtos.get(i).getNome());
            dlm.addElement("Marca: " + produtos.get(i).getMarca());
            dlm.addElement("Tipo: " + produtos.get(i).getTipo());
            dlm.addElement("Pre√ßo: " + produtos.get(i).getPreco());
            dlm.addElement("-----------------------------------------------");
        }


    }//GEN-LAST:event_btnListarActionPerformed

